// TypeScript interfaces for MCQ data structure
interface AIHook {
  targetWord: string;
  intent: string;
  questionLine: string;
  imagePrompt: string;
}

interface AItutor {
 target_word: string;
 question: string;
 student_entry: string;
 topic_to_reinforce: string;
 reading_rule: string,
spelling_pattern_or_rule: string;
}


interface MCQQuestion {
  id: number;
  topicId: string;
  topicName: string;
  progressTopicName: string;
  questionElements: string;
  answerElements: string;
  templateType: string;
  word: string;
  imageUrl: string | null;
  explanation: string;
  questionText: string;
  options: string[];
  correctAnswer: number;
  template: string;
  isSpacing: boolean;
  isSorting: boolean;
  isSpelling: boolean;
  aiHook: AIHook;
  passage?: string; // Optional for reading comprehension
  audio: string; // Audio text for speaker button
  isPrefilled?: boolean; // Optional: whether this spelling question has prefilled characters
  prefilledIndexes?: number[]; // Optional: array of character indexes that are prefilled
  aiTutor: AItutor;
}

interface DragDropQuestion {
  id: number;
  topicId: string;
  topicName: string;
  progressTopicName: string;
  questionElements: string;
  answerElements: string;
  templateType: string;
  word: string;
  imageUrl: string | null;
  explanation: string;
  questionText: string;
  sortingWords: string[];
  sortingBins: string[];
  correctAnswer: Record<string, string[]>;
  template: string;
  isSpacing: boolean;
  isSorting: boolean;
  isSpelling: boolean;
  aiHook: AIHook;
  passage?: string; // Optional for reading comprehension
  audio: string; // Audio text for speaker button
  isPrefilled?: boolean; // Optional: whether this spelling question has prefilled characters
  prefilledIndexes?: number[]; // Optional: array of character indexes that are prefilled
  aiTutor: AItutor;
}

interface FillBlankQuestion {
  id: number;
  topicId: string;
  topicName: string;
  questionElements: string;
  answerElements: string;
  progressTopicName: string;

  templateType: string;
  word: string;
  imageUrl: string | null;
  explanation: string;
  questionText: string;
  correctAnswer: string;
  template: string;
  isSpacing: boolean;
  isSorting: boolean;
  isSpelling: boolean;
  aiHook: AIHook;
  passage?: string; // Optional for reading comprehension
  audio: string; // Audio text for speaker button
  isPrefilled?: boolean; // Optional: whether this spelling question has prefilled characters
  prefilledIndexes?: number[]; // Optional: array of character indexes that are prefilled
  aiTutor: AItutor;
}

interface ReadingQuestion {
  id: number;
  topicId: string;
  topicName: string;
  questionElements: string;
  answerElements: string;
  progressTopicName: string;

  templateType: string;
  word: string;
  imageUrl: string | null;
  explanation: string;
  questionText: string;
  correctAnswer: string;
  template: string;
  isSpacing: boolean;
  isSorting: boolean;
  isSpelling: boolean;
  aiHook: AIHook;
  passage?: string; // Optional for reading comprehension
  audio: string; // Audio text for speaker button
  isReading: boolean;
  isPrefilled?: boolean; // Optional: whether this spelling question has prefilled characters
  prefilledIndexes?: number[]; // Optional: array of character indexes that are prefilled
  aiTutor: AItutor;
}

interface TopicInfo {
  topicId: string;
  topicName: string;
  progressTopicName: string;

  questionElements: string;
  answerElements: string;
  templateType: string;
}

interface Topic {
  topicInfo: TopicInfo;
  questions: (MCQQuestion | DragDropQuestion | FillBlankQuestion| ReadingQuestion)[];
}

export interface MCQData {
  topics: Record<string, Topic>;
}
